var Control=function(t){this.grids=t,this.speed=1,this.timeElapsed=0};Control.prototype.getCell=function(t,i){return t=(t+this.grids.numCols)%this.grids.numCols,i=(i+this.grids.numCols)%this.grids.numRows,this.grids.cellsArray[t+i*this.grids.numCols]},Control.prototype.getSurroundings=function(t,i){return[this.getCell(t-1,i-1),this.getCell(t-1,i),this.getCell(t-1,i+1),this.getCell(t,i-1),this.getCell(t,i+1),this.getCell(t+1,i-1),this.getCell(t+1,i),this.getCell(t+1,i+1)]},Control.prototype.shouldLive=function(t,i){var r=this.getSurroundings(t,i),e=r.filter(function(t){return t.isAlive});return 3===e.length?!0:!1},Control.prototype.shouldDie=function(t,i){var r=this.getSurroundings(t,i),e=r.filter(function(t){return t.isAlive});return e.length<2?!0:e.length>3?!0:!1},Control.prototype.update=function(){var t=this,i=this.grids.cellsArray.filter(function(i){return t.shouldLive(i.x,i.y)});i.forEach(function(t){t.isAlive=!0});var r=this.grids.cellsArray.filter(function(i){return t.shouldDie(i.x,i.y)});r.forEach(function(t){t.isAlive=!1})};var control=new Control(grids);