var Control=function(t){this.grids=t,this.speed=2,this.timeElapsed=0};Control.prototype.alive=function(){},Control.prototype.getCell=function(t,e){return t=(t+this.grids.numCols)%this.grids.numCols,e=(e+this.grids.numCols)%this.grids.numRows,this.grids.cellsArray[t+e*this.grids.numCols]},Control.prototype.getSurroundings=function(t,e){return[this.getCell(t-1,e-1),this.getCell(t-1,e),this.getCell(t-1,e+1),this.getCell(t,e-1),this.getCell(t,e+1),this.getCell(t+1,e-1),this.getCell(t+1,e),this.getCell(t+1,e+1)]},Control.prototype.shouldLive=function(t,e){var i=this.getSurroundings(t,e),r=i.filter(function(t){return t.isAlive});return 3===r.length?!0:!1},Control.prototype.shouldDie=function(t,e){var i=this.getSurroundings(t,e),r=i.filter(function(t){return t.isAlive});return r.length<2?!0:r.length>3?!0:!1},Control.prototype.update=function(t){var e=this;if(e.timeElapsed+=t,!(e.timeElapsed<e.speed)){e.timeElapsed=0,console.log("updated");var i=this.grids.cellsArray.filter(function(t){return e.shouldLive(t.x,t.y)});i.forEach(function(t){t.isAlive=!0});var r=this.grids.cellsArray.filter(function(t){return e.shouldDie(t.x,t.y)});r.forEach(function(t){t.isAlive=!1})}};