var Grids=function(i,t,h,s){this.numRows=i,this.numCols=t,this.width=h,this.height=s,this.canvasWidth=t*h,this.canvasHeight=i*s,this.gridBackground="lightgrey",this.aliveCellColour="green",this.cellsArray=[]};Grids.prototype.init=function(){for(var i=this,t=0;t<this.numRows*this.numCols;t++){var h=t%this.numCols,s=Math.floor(t/this.numCols);this.cellsArray[t]=new Cell(h,s,i.numCols,i.numRows)}},Grids.prototype.render=function(i){var t=this;i.fillStyle="white",i.fillRect(0,0,t.canvasWidth,t.canvasHeight);for(var h=0;h<t.numCols;h++)i.beginPath(),i.moveTo(h*t.width,0),i.lineTo(h*t.width,t.height*t.numRows),i.stroke();for(var s=0;s<t.numRows;s++)i.beginPath(),i.moveTo(0,s*t.height),i.lineTo(t.width*t.numCols,s*t.height),i.stroke();i.fillStyle=t.aliveCellColour;var e=t.cellsArray.filter(function(i){return i.isAlive});e.forEach(function(h){i.fillRect(h.x*t.width,h.y*t.height,t.width,t.height)})};var Cell=function(i,t){this.x=i,this.y=t,this.isAlive=!1};