var Grids=function(i,t,s,h){this.numRows=i,this.numCols=t,this.width=s,this.height=h,this.canvasWidth=t*s,this.canvasHeight=i*h,this.gridBackground="#faf8ef",this.aliveCellColour="green",this.cellsArray=[]};Grids.prototype.init=function(){for(var i=this,t=0;t<this.numRows*this.numCols;t++){var s=t%this.numCols,h=Math.floor(t/this.numCols);this.cellsArray[t]=new Cell(s,h,i.numCols,i.numRows)}},Grids.prototype.render=function(i){var t=this;i.fillStyle=this.gridBackground,i.fillRect(0,0,t.canvasWidth,t.canvasHeight);for(var s=0;s<t.numCols;s++)i.beginPath(),i.moveTo(s*t.width,0),i.lineTo(s*t.width,t.height*t.numRows),i.stroke();for(var h=0;h<t.numRows;h++)i.beginPath(),i.moveTo(0,h*t.height),i.lineTo(t.width*t.numCols,h*t.height),i.stroke();i.fillStyle=t.aliveCellColour;var e=t.cellsArray.filter(function(i){return i.isAlive});e.forEach(function(s){i.fillRect(s.x*t.width,s.y*t.height,t.width,t.height)})};var Cell=function(i,t){this.x=i,this.y=t,this.isAlive=!1},CELL_WIDTH=20,CELL_HEIGTH=20,NO_COLS=20,NO_ROWS=20,GRID_HEIGHT=CELL_HEIGTH*NO_ROWS,GRID_WIDTH=CELL_WIDTH*NO_COLS,grids=new Grids(NO_ROWS,NO_COLS,CELL_WIDTH,CELL_HEIGTH);