var Grids=function(i,t,h,l){this.numRows=i,this.numCols=t,this.width=h,this.height=l,this.gridBackground="lightgrey",this.aliveCellColour="green",this.cellsArray=[]};Grids.prototype.init=function(){for(var i=this,t=0;t<this.numRows*this.numCols;t++){var h=t%this.numCols,l=Math.floor(t/this.numCols);this.cellsArray[t]=new Cell(h,l,i.numCols,i.numRows)}},Grids.prototype.render=function(i){var t=this;i.fillStyle="white",i.fillRect(0,0,t.numCols*t.width,t.numRows*t.height);for(var h=0;h<t.numCols;h++)i.beginPath(),i.moveTo(h*t.width,0),i.lineTo(h*t.width,t.height*t.numRows),i.stroke();for(var l=0;l<t.numRows;l++)i.beginPath(),i.moveTo(0,l*t.height),i.lineTo(t.width*t.numCols,l*t.height),i.stroke();i.fillStyle=t.aliveCellColour;var o=t.cellsArray.filter(function(i){return i.isAlive});o.forEach(function(h){i.fillRect(h.x*t.width,h.y*t.height,t.width,t.height)})};var Cell=function(i,t){this.x=i,this.y=t,this.isAlive=!1};