var CELL_WIDTH=20,CELL_HEIGTH=20,NO_COLS=10,NO_ROWS=10,MATRIX_HEIGTH=CELL_HEIGTH*NO_ROWS,MATRIX_WIDTH=CELL_WIDTH*NO_COLS,Grids=function(i,t,s,o){this.numRows=i,this.numCols=t,this.width=s,this.height=o,this.gridBackground="lightgrey",this.cellColour="green",this.cellsArray=[]};Grids.prototype.init=function(){for(var i=this,t=0;t<this.numRows*this.numCols;t++){var s=t%this.numCols,o=Math.floor(t/this.numCols);this.cellsArray[t]=new Cell(s,o,i.numCols,i.numRows)}},Grids.prototype.render=function(i){var t=this;i.fillStyle="white",i.fillRect(0,0,t.numCols*t.width,t.numRows*t.height);for(var s=0;s<t.numCols;s++)i.beginPath(),i.moveTo(s*t.width,0),i.lineTo(s*t.width,t.height*t.numRows),i.stroke();for(var o=0;o<t.numRows;o++)i.beginPath(),i.moveTo(0,o*t.height),i.lineTo(t.width*t.numCols,o*t.height),i.stroke()};var Cell=function(i,t){this.x=i,this.y=t,this.isAlive=!1};